function addComment(ajax_grade, ajax_comment) {

                        $.ajax({

                        type: 'POST',

                        data: {
                        action: 'AddComment',
                        ajax_grade: ajax_grade,
                        ajax_comment: ajax_comment
                        },
                        success: function (output) {
                        console.log(output);
                        $('#ajax').empty();
                        $('#ajax').html(output);


                        },
                        error: function(error){
                        console.log(error);
                        }
                        });

                        }


                        $('.pagination').click(function (e) {
                        e.preventDefault();

                        $.ajax({

                        type: 'POST',

                        data: {
                        action: 'Pagination',
                        page: e.target.innerText
                        },
                        success: function (output) {
                        $('#ajax').empty();
                        $('#ajax').html(output);


                        },
                        error: function(error){
                        console.log(error);
                        }
                        });
                        });

